import React from 'react';
import {connect} from 'react-redux';
import * as R from 'ramda';
import {Link} from 'react-router-dom';

import {getTotalBasketPrice, getBasketPhoneWithCount} from 'selectors/selectors';
import {removePhoneFromBasket, cleanBasket, basketCheckout} from 'redux/actions';

const Basket = ({phones, totalPrice, removePhoneFromBasket, cleanBasket, basketCheckout}) => {

    const isBasketEmpty = R.isEmpty(phones);

    const renderContent = () => {
        return (
            <div>
                {isBasketEmpty && <div>Your shopping cart is empty</div>}
                <div className="table-responsive">
                    <table className="table-bordered table-striped table-condensed cf">
                        <tbody>
                        {phones.map((phone, index) => (
                            <tr key={index} className="item-checkout mt-4">
                                <td className="first-column-checkout">
                                    <img
                                        className="card-img"
                                        src={phone.image}
                                        alt={phone.name}
                                    />
                                </td>
                                <td>{phone.name}</td>
                                <td>${phone.price}</td>
                                <td>{phone.count}</td>
                                <td>
                                    <span
                                        className="delete-cart"
                                        onClick={() => removePhoneFromBasket(phone.id)}
                                    ></span>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                {R.not(isBasketEmpty) &&
                    <div className="row pull-right">
                        <div className="total-user-checkout">
                            <b>Total:</b>&nbsp;
                            ${totalPrice}
                        </div>
                    </div>
                }
            </div>
        )
    };

    const renderSideBar = () => (
        <div className="">
            <Link
                to="/"
                className="btn btn-info"
            >
                <i className="fa fa-check-circle"></i>&nbsp;
                <span>Continue shopping!</span>
                {/*<i className="fa fa-check-circle"></i>&nbsp;*/}
            </Link>
            {R.not(isBasketEmpty) &&
            <div>
                <button
                    onClick={cleanBasket}
                    className="btn btn-danger"
                >
                    <i className="fa fa-trash"></i>
                    &nbsp;Clean cart
                </button>
                <button
                    onClick={() => basketCheckout(phones)}
                    className="btn btn-success"
                >
                    <i className="fa fa-envelope"></i>&nbsp;
                    Checkout
                </button>
            </div>
            }
        </div>
    );

    return (
        <div className="view-container">
            <div className="container">
                <div className="row">
                    <div className="col-md-9">
                        {renderContent()}
                    </div>
                    <div className="col-md-3 btn-user-checkout">
                        {renderSideBar()}
                    </div>
                </div>
            </div>
        </div>
    )
};

const mapStateToProps = state => {
    return {
        phones: getBasketPhoneWithCount(state),
        totalPrice: getTotalBasketPrice(state)
    }
};

const mapDispatchToProps = {
    removePhoneFromBasket,
    cleanBasket,
    basketCheckout
};

export default connect(mapStateToProps, mapDispatchToProps)(Basket);