import React, {Component} from 'react';
import {connect} from 'react-redux';
import * as R from 'ramda';
import {Link} from 'react-router-dom';

import {fetchPhones, loadMorePhones, addPhoneToBasket, fetchCategories} from 'redux/actions';
import {getPhones} from 'selectors/selectors';
import Layout from 'containers/layout';



class Phones extends Component {

    componentDidMount() {
        this.props.fetchPhones();
        this.props.fetchCategories();
    }

    renderPhone = (phone, index) => {
        // const {description, image, name, price} = phone;
        const shortDescription = `${R.take(60, phone.description)}...`;// take only 60 symbols from phone.description
        const { addPhoneToBasket } =this.props;
        return (
          <div className="col-sm-4 col-lg-4 col-md-4 book-list" key={index}>
              <div className="card">
                  <img
                      className="card-img-top"
                      src={phone.image}
                      alt={phone.name}
                  />
                  <div className="card-body">
                      <h5 className="pull-right">${phone.price}</h5>
                      <h5>
                          <Link
                              to={`/phones/${phone.id}`}
                              className="card-title"
                          >
                              {phone.name}
                          </Link>
                      </h5>
                      <p className="card-text">{shortDescription}</p>
                      <p className="itemButton">
                          <button
                              className="btn btn-primary"
                              onClick={() => addPhoneToBasket(phone.id)}
                          >
                              Buy now!
                          </button>
                          <Link
                              to={`/phones/${phone.id}`}
                              className="btn btn-outline-secondary"
                          >
                              More info
                          </Link>
                      </p>
                  </div>
              </div>
          </div>
      )
    };

    render() {
        const { phones, loadMorePhones } = this.props;
        // console.log('phones', phones);
        return (
            <Layout>
                <div className="books row">
                    {phones.map((phone, index) => this.renderPhone(phone, index) )}
                </div>
                <div className='row'>
                    <div className="col-md-12">
                        <button
                            onClick={loadMorePhones}
                            className="pull-right btn btn-primary"
                        >
                            Load more
                        </button>
                    </div>
                </div>
            </Layout>
        );
    };
}

const mapStateToProps = (state, ownProps) => ({
    phones: getPhones(state, ownProps)
});

const mapDispatchToProps = {
    fetchPhones,
    fetchCategories,
    loadMorePhones,
    addPhoneToBasket
};

export default connect(mapStateToProps, mapDispatchToProps)(Phones);