import {combineReducers} from 'redux';
import {connectRouter} from 'connected-react-router';

import phones from 'redux/reducers/phones';
import {phonesPage} from 'redux/reducers/phones-page';
import {phonePage} from 'redux/reducers/phone-page';
import basket from 'redux/reducers/basket';
import categories from 'redux/reducers/categories';

export default history => combineReducers({
    router: connectRouter(history),
    phones,
    phonesPage,
    phonePage,
    basket,
    categories
})