import * as R from 'ramda';

import {FETCH_PHONE_BY_ID_SUCCESS} from 'redux/action-types';

const initialState = {
    id: null
};

export const phonePage = (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONE_BY_ID_SUCCESS:
            return R.merge(state, {
                id: R.prop('id', payload) // or id: payload.id
            });
        default:
            return state;
    }
};